,p_javascript_code=>wwv_flow_string.join(wwv_flow_t_varchar2(
'let currentUUID = null;',
'let pollingTimer = null;',
'window.startPolling = function () {',
'  //if (!currentUUID) return;',
'  console.log("Polling starting");',
'  if (window.pollingTimer) clearInterval(window.pollingTimer);',
'',
'  //const id = $v("P23_ID");',
'  const id = currentUUID; //$v("P23_ID");',
'  const baseUrl = $v("P23_URL");',
'  const interval = parseInt(document.getElementById("poll-interval").value);',
'',
'  // Show UI',
'  document.getElementById("refresh-section").style.display = "block";',
'  const progressEl = document.getElementById("progress");',
'  progressEl.style.display = "block";',
'  progressEl.textContent = "Status: waiting...";',
'  progressEl.style.backgroundColor = "#fff3cd";',
'  progressEl.style.color = "#856404";',
'',
'  document.getElementById("poll-interval").addEventListener("change",startPolling)',
'  window.pollingTimer = setInterval(() => pollResult(id, baseUrl), interval);',
'  pollResult(id, baseUrl); // Initial',
'};',
'',
'function pollResult(id, baseUrl) {',
'',
'    console.log("pollResult ...");',
'  if (!id || !baseUrl) return;',
'',
'  ',
'  fetch(`${baseUrl}/progress/${id}`)',
'    .then(resp => resp.json())',
'    .then(data => {',
'      const progress = document.getElementById("progress");',
'      const resultBox = document.getElementById("result-box");',
'',
'      if (data.status) {',
'        progress.className = data.status;',
'        progress.textContent = `Status: ${data.status}, Segments: ${data.segments || 0}`;',
'',
'        const bgMap = {',
'          done: "#d4edda",',
'          processing: "#fff3cd",',
'          cancelled: "#f8d7da",',
'          throttled: "#f5c6cb"',
'        };',
'        const colorMap = {',
'          done: "#155724",',
'          processing: "#856404",',
'          cancelled: "#721c24",',
'          throttled: "#721c24"',
'        };',
'        progress.style.backgroundColor = bgMap[data.status] || "#e2e3e5";',
'        progress.style.color = colorMap[data.status] || "#383d41";',
'      }',
'',
'      if (data.result) {',
'        resultBox.textContent = data.result;',
'        resultBox.scrollTop = resultBox.scrollHeight;',
'      }',
'',
'      if (data.status === "done" || data.status === "cancelled") {',
'        clearInterval(window.pollingTimer);',
'      }',
'    })',
'    .catch(err => {',
'      const progress = document.getElementById("progress");',
'      progress.textContent = "Error fetching progress.";',
'      progress.style.backgroundColor = "#f8d7da";',
'      progress.style.color = "#721c24";',
