'<div id="record-section" style="margin-top: 30px;">',
unistr('  <h3>\D83C\DF99\FE0F Record & Upload to Whisper</h3>'),
'',
'',
'',
'  <div style="display: flex; gap: 10px; flex-wrap: wrap;">',
