'  <!-- Mic animation -->',
'  <div id="mic-visualizer" style="height:20px; margin: 10px 0;">',
'    <div id="mic-pulse" style="width: 20px; height: 20px; border-radius: 50%; background: red; animation: pulse 1s infinite ease-in-out; display:none;"></div>',
'  </div>',
'  <div id="record-status" style="margin-top: 10px; font-weight: bold;"></div>',
'</div>',
'',
'<style>',
'  @keyframes pulse {',
'    0% { transform: scale(1); opacity: 0.6; }',
