'  <div id="live-transcript-box" style="white-space:pre-wrap; border:1px solid #ccc; padding:12px; height:150px; overflow-y:auto; font-family: monospace;"></div>',
'</div>',
'',
'<style>',
'  #live-transcript-box .interim {',
'    display: inline-block;',
'    padding: 2px 6px;',
'    margin: 2px;',
'    background-color: #333;',
'    color: #fff;',
'    font-style: italic;',
'    font-size: 1.1em;',
'    border-radius: 4px;',
'  }',
'',
'  .mic-circle {',
'    width: 14px;',
'    height: 14px;',
'    border-radius: 50%;',
'    background-color: red;',
'    margin-left: 10px;',
'    animation: pulse 1s infinite;',
'    display: inline-block;',
'    vertical-align: middle;',
'  }',
'',
'  @keyframes pulse {',
'    0% { transform: scale(1); opacity: 1; }',
