'  }',
'</style>',
'',
'<script>',
'  const liveTranscriber = (() => {',
'    const outputBox = document.getElementById("live-transcript-box");',
'    const btnStart = document.getElementById("btn-start");',
'    const btnPause = document.getElementById("btn-pause");',
'    const btnStop  = document.getElementById("btn-stop");',
'    const btnCopy  = document.getElementById("btn-copy");',
'    const micPulse = document.getElementById("mic-indicator");',
'',
'    const SpeechRecognition = window.SpeechRecognition || window.webkitSpeechRecognition;',
'    const recog = SpeechRecognition ? new SpeechRecognition() : null;',
'',
'    let finalTranscript = "";',
'    let hasStopped = true;',
'',
'    function toggleButtons(state) {',
'      btnStart.style.display = (state === ''initial'' || state === ''stopped'' || state === ''paused'') ? '''' : ''none'';',
'      btnPause.style.display = (state === ''recording'') ? '''' : ''none'';',
'      btnStop.style.display  = (state === ''recording'') ? '''' : ''none'';',
'      //btnCopy.style.display  = (state === ''recording'' || state === ''stopped'') ? '''' : ''none'';',
